mapper.insert:
insert into t_code (
	group_id
	, code_id
	, code_nm
	, code_dsc
	, use_yn
	, rgst_id
	, rgst_dt
	, modi_id
	, modi_dt
	, ord_seq
) values (
	#{groupId}
	, #{codeId}
	, #{codeNm}
	, #{codeDsc}
	, #{useYn}
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
	, #{ordSeq}
)

mapper.update:
update t_code
set
	code_nm = #{codeNm}
	, code_dsc = #{codeDsc}
	, modi_dt = now()
	, modi_id = #{modiId}
	, use_yn = #{useYn}
	, ord_seq = #{ordSeq}
where
	group_id = #{groupId}
and
	code_id = #{codeId}
