	, CODE_KEY
	, UP_COMPANY_CODE
	, CODE_NM
	, CODE_DSC
	, RPT_MAT_STRUCT
	, RPT_DEV_ANAL
	, RPT_VISUAL_JUDG
	, RPT_TEST
	, RPT_PERMISSION
	, RPT_ETC
	, USE_YN
	, RGST_ID
	, RGST_DT
	, MODI_ID
	, MODI_DT
	, ORD_SEQ
	, REVISION_YEAR
	, REVISION_MONTH
) VALUES (
	#{groupId}
	, #{codeId}
	, #{codeKey}
	, #{upCompanyCode}
	, #{codeNm}
	, #{codeDsc}
	, #{rptMatStruct}
	, #{rptDevAnal}
	, #{rptVisualJudg}
	, #{rptTest}
	, #{rptPermission}
	, #{rptEtc}
	, #{useYn}
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
	, #{ordSeq}
	, #{revisionYear}
	, #{revisionMonth}
)

mapper.update:
UPDATE T_ENVIRONMENT_CODE
SET
	CODE_NM = #{codeNm}
	, CODE_KEY = #{codeKey}
	, CODE_DSC = #{codeDsc}
	, MODI_DT = now()
	, MODI_ID = #{modiId}
	, USE_YN = #{useYn}
	, ORD_SEQ = #{ordSeq}
	, RPT_MAT_STRUCT = #{rptMatStruct}
	, RPT_DEV_ANAL = #{rptDevAnal}
	, RPT_VISUAL_JUDG = #{rptVisualJudg}
	, RPT_TEST = #{rptTest}
	, RPT_PERMISSION = #{rptPermission}
	, RPT_ETC = #{rptEtc}
WHERE
	GROUP_ID = #{groupId}
AND
	CODE_ID = #{codeId}
