mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY UPPER(CODE_NM) ASC) AS ROWNUM, GROUP_ID, CODE_ID,
UP_COMPANY_CODE,
CODE_KEY, CODE_NM, CODE_DSC, USE_YN, RGST_ID, RGST_DT, MODI_ID, MODI_DT FROM T_PACKAGING_CODE where GROUP_ID = #{groupId}
AND UP_COMPANY_CODE = #{upCompanyCode}
ORDER BY ROWNUM ASC

mapper.insert:
INSERT INTO T_PACKAGING_CODE (
	GROUP_ID
	, CODE_ID
	, CODE_KEY
	, UP_COMPANY_CODE
	, CODE_NM
	, CODE_DSC
	, USE_YN
	, RGST_ID
	, RGST_DT
	, MODI_ID
	, MODI_DT
	, ORD_SEQ
) VALUES (
	#{groupId}
	, #{codeId}
	, #{codeKey}
	, #{upCompanyCode}
	, #{codeNm}
	, #{codeDsc}
	, #{useYn}
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
	, #{ordSeq}
)

mapper.update:
UPDATE T_PACKAGING_CODE
SET
	CODE_NM = #{codeNm}
	, CODE_KEY = #{codeKey}
	, CODE_DSC = #{codeDsc}
	, MODI_DT = now()
	, MODI_ID = #{modiId}
	, USE_YN = #{useYn}
	, ORD_SEQ = #{ordSeq}
WHERE
	GROUP_ID = #{groupId} AND UP_COMPANY_CODE = #{upCompanyCode}
AND
	CODE_ID = #{codeId}
