mapper.update:
UPDATE T_FILE SET
	  MODI_ID = #{modiId}
	, MODI_DT = NOW()

		, STORAGE_SE = #{storageSe}
		, SAVE_PATH = #{savePath}
		, BUCKET_NM = #{bucketNm}
		, SAVE_FILE_NM = #{saveFileNm}
		, FILE_URL = #{fileUrl}	


	    , FILE_SIZE = #{fileSize}
	    , ATMC_DEL_YN = CASE WHEN #{fileSize} > (SELECT CODE_NM FROM T_CODE WHERE GROUP_ID = 'FILE' AND CODE_ID = 'LIMIT') THEN 'Y' ELSE 'N' END
        , ATMC_DEL_DT = CASE WHEN #{fileSize} > (SELECT CODE_NM FROM T_CODE WHERE GROUP_ID = 'FILE' AND CODE_ID = 'LIMIT')
                             THEN CURRENT_DATE + (SELECT CODE_NM FROM T_CODE WHERE GROUP_ID = 'FILE' AND CODE_ID = 'PD')
                        ELSE NULL END


		, SAVE_FILE_VER = #{saveFileVer}


		, REF_ID = #{refId}

WHERE FILE_ID = #{fileId}

mapper.delete:
UPDATE T_FILE SET
    USE_YN = #{useYn}
  , MODI_ID = #{modiId}
  , MODI_DT = NOW()
WHERE FILE_URL = #{fileUrl}

UPDATE T_FILE SET
    USE_YN = #{useYn}
  , MODI_ID = #{modiId}
  , MODI_DT = NOW()
WHERE REF_ID = #{refId}

mapper.select:
SELECT FILE_ID , STORAGE_SE , SAVE_PATH , SAVE_FILE_NM , FILE_NM , FILE_EXTSN , FILE_SIZE , USE_YN , RGST_ID , RGST_DT , MODI_ID , MODI_DT , BUCKET_NM , FILE_URL , FILE_CL , SAVE_FILE_VER , REF_ID , REF_VER , ATMC_DEL_YN , ATMC_DEL_DT FROM T_FILE WHERE USE_YN = 'Y'
AND FILE_ID = #{fileId}
AND REF_ID = #{refId}
AND FILE_CL = #{fileCl}
ORDER BY FILE_ID

SELECT FILE_ID , STORAGE_SE , SAVE_PATH , SAVE_FILE_NM , FILE_NM , FILE_EXTSN , FILE_SIZE , USE_YN , RGST_ID , RGST_DT , MODI_ID , MODI_DT , BUCKET_NM , FILE_URL , FILE_CL , SAVE_FILE_VER , REF_ID , REF_VER , ATMC_DEL_YN , ATMC_DEL_DT FROM T_FILE WHERE USE_YN = 'Y'
AND FILE_ID = #{fileId}
AND FILE_URL = #{fileUrl}
