mapper.insert:
insert into t_user_auth (user_id, auth_id, use_yn, rgst_id, rgst_dt, modi_id, modi_dt)
values (#{userId}, #{authId}, #{useYn}, #{rgstId}, now(), #{modiId}, now())
ON DUPLICATE KEY UPDATE
	use_yn = #{useYn}
  , auth_id = #{authId}
  , modi_dt = now()
  , modi_id = #{modiId}

INSERT INTO t_group (user_id, auth_id, use_yn, rgst_id, rgst_dt, modi_id, modi_dt)
values (#{userId}, #{authId}, #{useYn}, #{rgstId}, now(), #{modiId}, now())
ON DUPLICATE KEY UPDATE
	use_yn = #{useYn}

  , auth_id = #{authId}

  , modi_dt = now()
  , modi_id = #{modiId}

INSERT INTO t_group (user_id, auth_id, use_yn, rgst_id, rgst_dt, modi_id, modi_dt)
values (#{userId}, #{authId}, #{useYn}, #{rgstId}, now(), #{modiId}, now())
ON DUPLICATE KEY UPDATE
	use_yn = #{useYn}
  , auth_id = #{authId}
  , modi_dt = now()
  , modi_id = #{modiId}

mapper.update:
update t_user
set
  company_code = #{companyCode}
  , email = #{email}
  , phone = #{phone}
  , dtLimitYn = #{dtLimitYn}
  , use_yn = #{useYn}
  , start_dt = #{startDt}
  , end_dt = #{endDt}
  , modi_dt = now()
  , modi_id = #{modiId}
where
	user_id = #{userId}

mapper.insert:
		INSER INTO t_group(
    		  USER_ID
    		, AUTH_ID
    		, USE_YN
    		, RGST_ID
			, RGST_DT
			, MODI_ID
			, MODI_DT
    	)VALUES(
    		#{userId}
    		, #{authId}
    		, #{useYn}
    		, #{rgstId}
    		, NOW()
    		, #{modiId}
    		, NOW()
    	)

mapper.update:
		UPDATE t_group
        SET
			  AUTH_ID = #{authId}
            , USE_YN = #{useYn}
            , MODI_ID = #{modiId}
            , MODI_DT = now()
        WHERE
            USER_ID = #{userId}
