mapper.update:
UPDATE T1_PRODUCT
SET
	  PRODUCT_NM          = #{productNm}
	, SUPPLIER_INFO       = #{supplierInfo}
	, PRODUCT_MAKER       = #{productMaker}
	, SUMMARY             = #{summary}
	, PHOTO_GFILE_ID      = #{photoGfileId}
	, SPEC_GFILE_ID       = #{specGfileId}
	, MASTER_APPLY_CODE   = #{masterApplyCode}
	, RECEIPT_NO          = #{receiptNo}
	, APPROVAL_NO         = #{approvalNo}
	, MASTER_MAPPING_CODE = #{masterMappingCode}
	, MAPPING_PRODUCT_CODE   = #{mappingProductCode}
WHERE
	upper(PRODUCT_CODE) = upper(#{productCode})

UPDATE T1_PRODUCT
SET
	RECYCLE_GRADE = #{recycleGrade}
WHERE
	PRODUCT_ID = #{productId}

mapper.insert:

INSERT INTO t1_product_packaging (
	  PACKAGING_ID   
	, PRODUCT_CODE     
	, PACKAGING_ORDER
	, PACKAGING_ORDER_NM
	, GROUP_ID       
	, CODE_ID      
	, USE_YN         
	, RGST_ID        
	, RGST_DT        
	, MODI_ID        
	, MODI_DT        
) VALUES (
	  #{packagingId}
	, #{productCode}
	, #{packagingOrder}
	, IF(#{packagingOrder}<10, CONCAT(CAST(#{packagingOrder} AS CHAR),'차포장'), '부속포장')
	, #{groupId}
	, #{codeId}
	, 'Y'
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
)

