	, SPEC_GFILE_ID
	, USE_YN
	, RGST_ID
	, RGST_DT
	, MODI_ID
	, MODI_DT
) VALUES (
	  #{productCode}
	, #{productNm}
	, #{productClass}
	, #{supplierInfo}
	, #{productMaker}
	, #{summary}
	, #{photoGfileId}
	, #{specGfileId}
	, 'Y'
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
)

mapper.update:
UPDATE T1_PRODUCT
SET
	  PRODUCT_NM          = #{productNm}
	, SUPPLIER_INFO       = #{supplierInfo}
	, PRODUCT_MAKER       = #{productMaker}
	, SUMMARY             = #{summary}
	, PHOTO_GFILE_ID      = #{photoGfileId}
	, SPEC_GFILE_ID       = #{specGfileId}
	, MASTER_APPLY_CODE   = #{masterApplyCode}
	, RECEIPT_NO          = #{receiptNo}
	, APPROVAL_NO         = #{approvalNo}
	, MASTER_MAPPING_CODE = #{masterMappingCode}
	, MAPPING_PRODUCT_CODE   = #{mappingProductCode}
WHERE
	PRODUCT_CODE = #{productCode}

UPDATE T1_PRODUCT
SET
	RECYCLE_GRADE = #{recycleGrade}
WHERE
	PRODUCT_ID = #{productId}

mapper.insert:
INSERT INTO t_product_packaging (
	  PACKAGING_ID   
	, PRODUCT_ID     
	, PACKAGING_NM   
	, PACKAGING_DSC  
	, PACKAGING_ORDER
	, MAT_TYPE       
	, PART_TYPE      
	, MAT_INFO       
	, WEIGHT         
	, STANDARD       
	, COLOR          
	, ADD_EXPLAN     
	, MAT_FILE_ID    
	, SUPPLIER_CODE 
	, MANAGER_ID 
	, USE_YN         
	, RGST_ID        
	, RGST_DT        
	, MODI_ID        
	, MODI_DT        
) VALUES (
	  #{packagingId}
	, #{productId}
	, #{packagingNm}
	, #{packagingDsc}
	, #{packagingOrder}
	, #{matType}
	, #{partType}
	, #{matInfo}
	, #{weight}
	, #{standard}
	, #{color}
	, #{addExplan}
	, #{matFileId}
	, #{supplierCode}
	, #{managerId}
	, 'Y'
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
)

mapper.update:
UPDATE t_product_packaging
SET
	  PACKAGING_ID         = #{packagingId}      
	, PRODUCT_ID           = #{productId}        
	, PACKAGING_NM         = #{packagingNm}      
	, PACKAGING_DSC        = #{packagingDsc}     
	, PACKAGING_ORDER      = #{packagingOrder}   
	, MAT_TYPE             = #{matType}          
	, PART_TYPE            = #{partType}         
	, MAT_INFO             = #{matInfo}          
	, WEIGHT               = #{weight}           
	, STANDARD             = #{standard}         
	, COLOR                = #{color}        
	, ADD_EXPLAN           = #{addExplan}      
	, MAT_FILE_ID          = #{matFileId}     
	, SUPPLIER_CODE        = #{supplierCode} 
	, MANAGER_ID           = #{managerId} 
	, MODI_ID              = #{modiId}           
	, MODI_DT              = now()         
WHERE
	PACKAGING_ID = #{packagingId}

UPDATE t_product_packaging SET USE_YN = 'N' , MODI_ID = #{modiId} , MODI_DT = now() WHERE PRODUCT_ID = #{productId}
and (PACKAGING_ORDER = #{packagingOrder})
and (PACKAGING_ID = #{packagingId})

mapper.insert:
INSERT INTO T_PRODUCT_RECYLE_CONTRIBUTIONS (
	  PRODUCT_ID
	, BASE_YEAR
	, ACCUMULATE_SALE_QTY
	, PACKING_TOTAL_WEIGHT
	, RECYLE_CONTRIBUTIONS
	, USE_YN
	, RGST_ID
	, RGST_DT
	, MODI_ID
	, MODI_DT
) VALUES (
	  #{productId}
	, #{baseYear}
	, #{accumulateSaleQty}
	, #{packingTotalWeight}
	, #{recyleContributions}
	, 'Y'
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
)   
ON DUPLICATE KEY UPDATE
      ACCUMULATE_SALE_QTY   = #{accumulateSaleQty} 
    , PACKING_TOTAL_WEIGHT  = #{packingTotalWeight} 
    , RECYLE_CONTRIBUTIONS  = #{recyleContributions}
    , MODI_ID               = #{modiId}           
	, MODI_DT               = now()

