mapper.insert:
INSERT INTO t_group_file (
	  GFILE_ID
	, FILE_ID
	, DEL_YN
	, USE_YN
	, RGST_ID
	, RGST_DT
	, MODI_ID
	, MODI_DT
) VALUES (
	  #{gfileId}
	, #{fileId}
	, 'N'
	, 'Y'
	, #{rgstId}
	, now()
	, #{modiId}
	, now()
)

mapper.update:
UPDATE t_group_file
SET
	  USE_YN              = 'N'
	, MODI_ID  = #{modiId}
	, MODI_DT  = now()
WHERE GFILE_ID = #{gfileId} 
  AND FILE_ID = #{fileId}

UPDATE t_group_file
SET
	  DEL_YN   = 'Y'
	, MODI_ID  = #{modiId}
	, MODI_DT  = now()
WHERE FILE_ID = #{fileId}
