mapper.insert:
INSERT INTO t_report (
	REPORT_ID
	, REPORT_NM
	, REPORT_URL
	, REPORT_DSC
	, COMPANY_ID
	, GROUP_ID
	, REPORT_TYPE
	, REPORT_SIZE
	, USE_YN
	, RGST_ID
	, RGST_DT
	, MODI_ID
	, MODI_DT
) VALUES (
	#{reportId}
	, #{reportNm}
	, #{reportUrl}
	, #{reportDsc}
	, #{companyId}
	, #{groupId}
	, #{reportType}
	, #{reportSize}
	, #{useYn}
	, #{rgstId}
	, NOW()
	, #{modiId}
	, NOW()
)

mapper.update:
UPDATE t_report SET
	GROUP_ID = #{groupId}
	, REPORT_TYPE = #{reportType}
	, REPORT_DSC = #{reportDsc}
	, REPORT_NM = #{reportNm}
	, REPORT_URL = #{reportUrl}
	, COMPANY_ID = #{companyId}
	, REPORT_SIZE = #{reportSize}
	, USE_YN = #{useYn}
	, MODI_ID = #{modiId}
	, MODI_DT = NOW()
WHERE
	REPORT_ID = #{reportId}
